from EfiPy  import *

from EfiPy.MdePkg.IndustryStandard.SmBios import SMBIOS_TABLE_STRING, SMBIOS_STRUCTURE

INTEL_FIRMWARE_VERSION_INFO_GROUP_NAME    = "Firmware Version Info"

class INTEL_FIRMWARE_VERSION (Structure):
  _pack_   = 1
  _fields_ = [
    ("MajorVersion",  UINT8),
    ("MinorVersion",  UINT8),
    ("Revision",      UINT8),
    ("BuildNumber",   UINT16),
  ]

class INTEL_FIRMWARE_VERSION_INFO (Structure):
  _pack_   = 1
  _fields_ = [
    ("ComponentName", SMBIOS_TABLE_STRING),
    ("VersionString", SMBIOS_TABLE_STRING),
    ("Version",       INTEL_FIRMWARE_VERSION),
  ]

class SMBIOS_TABLE_TYPE_OEM_INTEL_FVI (Structure):
  _pack_   = 1
  _fields_ = [
    ("Header",  SMBIOS_STRUCTURE),
    ("Count",   UINT8),
    ("Fvi",     INTEL_FIRMWARE_VERSION_INFO * 1),
  ]
