from EfiPy  import *

class EFI_DEBUG_MASK_PROTOCOL (Structure):
  pass

EFI_DEBUG_MASK_REVISION        = 0x00010000

EFI_GET_DEBUG_MASK = CFUNCTYPE (
  EFI_STATUS,
  POINTER(EFI_DEBUG_MASK_PROTOCOL), # IN     *This
  POINTER(UINTN)                    # IN OUT *CurrentDebugMask
  )

EFI_SET_DEBUG_MASK = CFUNCTYPE (
  EFI_STATUS,
  POINTER(EFI_DEBUG_MASK_PROTOCOL), # IN *This
  UINTN                             # IN NewDebugMask
  )

EFI_DEBUG_MASK_PROTOCOL._fields_ = [
  ("Revision",      INT64),
  ("GetDebugMask",  EFI_GET_DEBUG_MASK),
  ("SetDebugMask",  EFI_SET_DEBUG_MASK),
  ]

gEfiDebugMaskProtocolGuid         = \
  EFI_GUID (0x4c8a2451, 0xc207, 0x405b, (0x96, 0x94, 0x99, 0xea, 0x13, 0x25, 0x13, 0x41))

DEBUG_MASK_VARIABLE_NAME  = u"EFIDebug"
gEfiGenericVariableGuid         = \
  EFI_GUID (0x59d1c24f, 0x50f1, 0x401a, (0xb1, 0x01, 0xf3, 0x3e, 0x0d, 0xae, 0xd4, 0x43))

