from EfiPy  import *

class PCI_ROOT_BRIDGE_APERTURE (Structure):
  _fields_ = [
  ("Base",  UINT64),
  ("Limit", UINT64),
  ]

class PCI_ROOT_BRIDGE (Structure):
  _fields_ = [
  ("Segment",               UINT32),
  ("Supports",              UINT64),
  ("Attributes",            UINT64),
  ("DmaAbove4G",            BOOLEAN),
  ("NoExtendedConfigSpace", BOOLEAN),
  ("ResourceAssigned",      BOOLEAN),
  ("AllocationAttributes",  UINT64),
  ("Bus",                   PCI_ROOT_BRIDGE_APERTURE),
  ("Io",                    PCI_ROOT_BRIDGE_APERTURE),
  ("Mem",                   PCI_ROOT_BRIDGE_APERTURE),
  ("MemAbove4G",            PCI_ROOT_BRIDGE_APERTURE),
  ("PMem",                  PCI_ROOT_BRIDGE_APERTURE),
  ("PMemAbove4G",           PCI_ROOT_BRIDGE_APERTURE),
  ("DevicePath",            POINTER(EFI_DEVICE_PATH_PROTOCOL)),
  ]

