from EfiPy  import *

class EFI_FILE_EXPLORER_PROTOCOL (Structure):
  pass

CHOOSE_HANDLER = CFUNCTYPE (
  BOOLEAN,
  POINTER(EFI_DEVICE_PATH_PROTOCOL) #   IN *FilePath
  )

CHOOSE_FILE = CFUNCTYPE (
  EFI_STATUS,
  POINTER(EFI_DEVICE_PATH_PROTOCOL),          #   IN *RootDirectory
  POINTER(CHAR16),                            #   IN *FileType,  OPTIONAL
  CHOOSE_HANDLER,                             #   IN ChooseHandler,  OPTIONAL
  POINTER(POINTER(EFI_DEVICE_PATH_PROTOCOL))  #   IN **File  OPTIONAL
  )

class _EFI_FILE_EXPLORER_PROTOCOL (Structure):
  _fields_ = [
  ("ChooseFile",  CHOOSE_FILE)
  ]

gEfiFileExplorerProtocolGuid      = \
  EFI_GUID (0x2C03C536, 0x4594, 0x4515, (0x9E, 0x7A, 0xD3, 0xD2, 0x04, 0xFE, 0x13, 0x63))

