from EfiPy  import *

from EfiPy.MdePkg.IndustryStandard.Acpi import EFI_ACPI_ADDRESS_SPACE_DESCRIPTOR

class NON_DISCOVERABLE_DEVICE (Structure):
  pass

NonDiscoverableDeviceDmaTypeCoherent    = 0
NonDiscoverableDeviceDmaTypeNonCoherent = 1
NonDiscoverableDeviceDmaTypeMax         = 2
NON_DISCOVERABLE_DEVICE_DMA_TYPE        = UINTN

NON_DISCOVERABLE_DEVICE_INIT = CFUNCTYPE (
  EFI_STATUS,
  POINTER(NON_DISCOVERABLE_DEVICE)  # IN *This
  )

NON_DISCOVERABLE_DEVICE._fields_ = [
    ("Type",        POINTER(EFI_GUID)),
    ("DmaType",     NON_DISCOVERABLE_DEVICE_DMA_TYPE), 
    ("Initialize",  NON_DISCOVERABLE_DEVICE_INIT), 
    ("Resources",   POINTER(EFI_ACPI_ADDRESS_SPACE_DESCRIPTOR)), 
  ]

gEdkiiNonDiscoverableDeviceProtocolGuid  = \
  EFI_GUID (0x0d51905b, 0xb77e, 0x452a, (0xa2, 0xc0, 0xec, 0xa0, 0xcc, 0x8d, 0x51, 0x4a))

