from EfiPy  import *

from EfiPy.MdePkg.Protocol.HiiImage import EFI_IMAGE_INPUT

class EDKII_PLATFORM_LOGO_PROTOCOL (Structure):
  pass

EdkiiPlatformLogoDisplayAttributeLeftTop       = 0
EdkiiPlatformLogoDisplayAttributeCenterTop     = 1
EdkiiPlatformLogoDisplayAttributeRightTop      = 2
EdkiiPlatformLogoDisplayAttributeCenterRight   = 3
EdkiiPlatformLogoDisplayAttributeRightBottom   = 4
EdkiiPlatformLogoDisplayAttributeCenterBottom  = 5
EdkiiPlatformLogoDisplayAttributeLeftBottom    = 6
EdkiiPlatformLogoDisplayAttributeCenterLeft    = 7
EdkiiPlatformLogoDisplayAttributeCenter        = 8
EDKII_PLATFORM_LOGO_DISPLAY_ATTRIBUTE          = UINTN

EDKII_PLATFORM_LOGO_GET_IMAGE = CFUNCTYPE (
  EFI_STATUS,
  POINTER(EDKII_PLATFORM_LOGO_PROTOCOL),          #   IN *This,
  POINTER(UINT32),                                #   IN OUT *Instance,
  POINTER(EFI_IMAGE_INPUT),                       #      OUT *Image,
  POINTER(EDKII_PLATFORM_LOGO_DISPLAY_ATTRIBUTE), #      OUT *Attribute,
  POINTER(INTN),                                  #      OUT *OffsetX,
  POINTER(INTN),                                  #      OUT *OffsetY
  )

EDKII_PLATFORM_LOGO_PROTOCOL._fields_ = [
    ("GetImage", EDKII_PLATFORM_LOGO_GET_IMAGE)
  ]

gEdkiiPlatformLogoProtocolGuid  = \
  EFI_GUID (0x53cd299f, 0x2bc1, 0x40c0, (0x8c, 0x07, 0x23, 0xf6, 0x4f, 0xdb, 0x30, 0xe0))

