from EfiPy  import *

from EfiPy.MdePkg.IndustryStandard.Acpi import EFI_ACPI_6_1_GENERIC_ADDRESS_STRUCTURE

ACPI_LPI_STRUCTURE_TYPE_NATIVE_CSTATE     = 0x00

class ACPI_LPI_STATE_FLAGS_Bits (Structure):
  _pack_   = 1
  _fields_ = [
  ("Disabled",            UINT32, 1),
  ("CounterUnavailable",  UINT32, 1),
  ("Reserved",            UINT32, 30),
  ]

class ACPI_LPI_STATE_FLAGS (Union):
  _pack_   = 1
  _fields_ = [
  ("Bits",    ACPI_LPI_STATE_FLAGS_Bits),
  ("Data32",  UINT32),
  ]

class ACPI_LPI_NATIVE_CSTATE_DESCRIPTOR (Structure):
  _pack_   = 1
  _fields_ = [
  ("Type",                      UINT32),
  ("Length",                    UINT32),
  ("UniqueId",                  UINT16),
  ("Reserved",                  UINT8 * 2),
  ("Flags",                     ACPI_LPI_STATE_FLAGS),
  ("EntryTrigger",              EFI_ACPI_6_1_GENERIC_ADDRESS_STRUCTURE),
  ("Residency",                 UINT32),
  ("Latency",                   UINT32),
  ("ResidencyCounter",          EFI_ACPI_6_1_GENERIC_ADDRESS_STRUCTURE),
  ("ResidencyCounterFrequency", UINT64),
  ]

