import EfiPy as e
import EfiPy.MdePkg.Protocol.DevicePath as DevP

def NextDevicePathNode (Node):
  return (DevP.EFI_DEVICE_PATH_PROTOCOL * 1).from_address(e.addressof (Node[0]) + Node[0].Length)

def SetDevicePathEndNode (Node):
  Node[0].Type = DevP.END_DEVICE_PATH_TYPE
  Node[0].SubType = DevP.END_ENTIRE_DEVICE_PATH_SUBTYPE
  Node[0].Length = e.sizeof (DevP.EFI_DEVICE_PATH_PROTOCOL)
