from EfiPy import *

class EFI_UNICODE_STRING_TABLE (Structure):
  _fields_ = [
    ("Language",      POINTER(CHAR8)),
    ("UnicodeString", POINTER(CHAR16)),
    ]

EfiLockUninitialized = 0
EfiLockReleased      = 1
EfiLockAcquired      = 2
EFI_LOCK_STATE       = UINTN

class EFI_LOCK (Structure):
  _fields_ = [
    ("Tpl",      EFI_TPL),
    ("OwnerTpl", EFI_TPL),
    ("Lock",     EFI_LOCK_STATE),
    ]

