from EfiPy import *

class EFI_TLS_CONFIGURATION_PROTOCOL (Structure):
  pass

EfiTlsConfigDataTypeHostPublicCert      = 0
EfiTlsConfigDataTypeHostPrivateKey      = 1
EfiTlsConfigDataTypeCACertificate       = 2
EfiTlsConfigDataTypeCertRevocationList  = 3
EfiTlsConfigDataTypeMaximum             = 4
EFI_TLS_CONFIG_DATA_TYPE                = UINTN

EFI_TLS_CONFIGURATION_SET_DATA = CFUNCTYPE (
  EFI_STATUS,
  POINTER(EFI_TLS_CONFIGURATION_PROTOCOL),  #   IN *This,
  EFI_TLS_CONFIG_DATA_TYPE,                 #   IN DataType,
  PVOID,                                    #   IN *Data,
  UINTN                                     #   IN DataSize
  )

EFI_TLS_CONFIGURATION_GET_DATA = CFUNCTYPE (
  EFI_STATUS,
  POINTER(EFI_TLS_CONFIGURATION_PROTOCOL),  #   IN     *This,
  EFI_TLS_CONFIG_DATA_TYPE,                 #   IN     DataType,
  PVOID,                                    #   IN OUT *Data,  OPTIONAL
  POINTER(UINTN)                            #   IN OUT *DataSize
  )

EFI_TLS_CONFIGURATION_PROTOCOL._fields_ = [
  ("SetData",      EFI_TLS_CONFIGURATION_SET_DATA),
  ("GetData",      EFI_TLS_CONFIGURATION_GET_DATA),
  ]

gEfiTlsConfigurationProtocolGuid = EFI_GUID(0x1682fe44, 0xbd7a, 0x4407, (0xb7, 0xc7, 0xdc, 0xa3, 0x7c, 0xa3, 0x92, 0x2d))
