from EfiPy import *

PCI_BUS_NUMBER_ICH                = 0x00
PCI_DEVICE_NUMBER_ICH_LPC           = 31
PCI_FUNCTION_NUMBER_ICH_LPC          = 0

R_ICH_LPC_ACPI_BASE                   = 0x40
B_ICH_LPC_ACPI_BASE_BAR                 = 0x0000FF80
R_ICH_LPC_ACPI_CNT                    = 0x44
B_ICH_LPC_ACPI_CNT_ACPI_EN              = 0x80

R_ACPI_PM1_TMR                        = 0x08
V_ACPI_TMR_FREQUENCY                    = 3579545
V_ACPI_PM1_TMR_MAX_VAL                  = 0x1000000

def PCI_ICH_LPC_ADDRESS(Register):
  return ((UINTN)(PCI_LIB_ADDRESS (PCI_BUS_NUMBER_ICH, PCI_DEVICE_NUMBER_ICH_LPC, PCI_FUNCTION_NUMBER_ICH_LPC, Register)))

