from EfiPy import *

class OPAL_DISK_ACTIONS (Structure):
  _pack_   = 1
  _fields_ = [
    ("PsidRevert",              UINT16, 1),
    ("Revert",                  UINT16, 1),
    ("RevertKeepDataForced",    UINT16, 1),
    ("AdminPass",               UINT16, 1),
    ("UserPass",                UINT16, 1),
    ("Unlock",                  UINT16, 1),
    ("SecureErase",             UINT16, 1),
    ("DisableUser",             UINT16, 1),
    ]

class OPAL_DISK_AND_PASSWORD_INFO (Structure):
  _pack_   = 1
  _fields_ = [
    ("Link",            UINT16),
    ("Password",        UINT16 * 32),
    ("PasswordLength",  UINT16),
    ("OpalDevicePath",  UINT16),
    ]

