from EfiPy import *

from EfiPy.MdePkg.IndustryStandard.TcgStorageCore import TCG_COM_PACKET, TCG_PACKET, TCG_SUB_PACKET, TCG_TOKEN_TYPE

TcgResultSuccess                = 0
TcgResultFailure                = 1
TcgResultFailureNullPointer     = 2
TcgResultFailureZeroSize        = 3
TcgResultFailureInvalidAction   = 4
TcgResultFailureBufferTooSmall  = 5
TcgResultFailureEndBuffer       = 6
TcgResultFailureInvalidType     = 7
TCG_RESULT                      = UINTN

class TCG_CREATE_STRUCT (Structure):
  _pack_   = 1
  _fields_ = [
    ("Buffer",        PVOID),
    ("BufferSize",    UINT32),
    ("ComPacket",     POINTER(TCG_COM_PACKET)),
    ("CurPacket",     POINTER(TCG_PACKET)),
    ("CurSubPacket",  POINTER(TCG_SUB_PACKET)),
    ("DryRun",        BOOLEAN),
    ]

class TCG_PARSE_STRUCT (Structure):
  _pack_   = 1
  _fields_ = [
    ("Buffer",        PVOID),
    ("BufferSize",    UINT32),
    ("ComPacket",     POINTER(TCG_COM_PACKET)),
    ("CurPacket",     POINTER(TCG_PACKET)),
    ("CurSubPacket",  POINTER(TCG_SUB_PACKET)),
    ("CurPtr",        POINTER(UINT8)),
    ]

class TCG_TOKEN (Structure):
  _pack_   = 1
  _fields_ = [
    ("Type",      TCG_TOKEN_TYPE),
    ("HdrStart",  POINTER(UINT8)),
    ]

