from EfiPy import *

from EfiPy.MdePkg.Protocol.StorageSecurityCommand import EFI_STORAGE_SECURITY_COMMAND_PROTOCOL

class OPAL_DISK_SUPPORT_ATTRIBUTE (Structure):
  _pack_   = 1
  _fields_ = [
    ("OpalSsc1",          UINT32, 1),
    ("OpalSsc2",          UINT32, 1),
    ("OpalSscLite",       UINT32, 1),
    ("PyriteSsc",         UINT32, 1),
    ("Sp1",               UINT32, 1),
    ("Sp2",               UINT32, 1),
    ("SpIeee1667",        UINT32, 1),
    ("MediaEncryption",   UINT32, 1),
    ("InitCpinIndicator", UINT32, 1),
    ("CpinUponRevert",    UINT32, 1),
    ("BlockSid",          UINT32, 1),
    ]

OpalOwnershipUnknown  = 0
OpalOwnershipNobody   = 1
OPAL_OWNER_SHIP       = UINTN

class OPAL_SESSION (Structure):
  _pack_   = 1
  _fields_ = [
    ("HostSessionId",   UINT32),
    ("TperSessionId",   UINT32),
    ("ComIdExtension",  UINT16),
    ("OpalBaseComId",   UINT16),
    ("*Sscp",           EFI_STORAGE_SECURITY_COMMAND_PROTOCOL),
    ("MediaId",         UINT32),
    ]

