from EfiPy import *

EDKII_SYSTEM_FIRMWARE_IMAGE_DESCRIPTOR_SIGNATURE  = SIGNATURE_32('S', 'F', 'I', 'D')

class EDKII_SYSTEM_FIRMWARE_IMAGE_DESCRIPTOR (Structure):
  _pack_   = 1
  _fields_ = [
    ("Signature",                       UINT32),
    ("HeaderLength",                    UINT32),
    ("Length",                          UINT32),
    ("PackageVersion",                  UINT32),
    ("PackageVersionNameStringOffset",  UINT32),
    ("ImageIndex",                      UINT8),
    ("Reserved",                        UINT8 *3),
    ("ImageTypeId",                     EFI_GUID),
    ("ImageId",                         UINT64),
    ("ImageIdNameStringOffset",         UINT32),
    ("Version",                         UINT32),
    ("VersionNameStringOffset",         UINT32),
    ("Reserved2",                       UINT8 * 4),
    ("Size",                            UINT64),
    ("AttributesSupported",             UINT64),
    ("AttributesSetting",               UINT64),
    ("Compatibilities",                 UINT64),
    ("LowestSupportedImageVersion",     UINT32),
    ("LastAttemptVersion",              UINT32),
    ("LastAttemptStatus",               UINT32),
    ("Reserved3",                       UINT8 * 4),
    ("HardwareInstance",                UINT64),
    # ("ImageIdNameStr",                  CHAR16 * N),
    # ("VersionNameStr",                  CHAR16 * N),
    # ("PackageVersionNameStr",           CHAR16 * N),
    ]

