from EfiPy                      import *

Msr               = 0
ControlRegister   = 1
MemoryMapped      = 2
CacheControl      = 3
REGISTER_TYPE     = UINTN

class CPU_REGISTER_TABLE_ENTRY (Structure):
  _fields_ = [
    ("RegisterType",    REGISTER_TYPE),
    ("Index",           UINT32),
    ("ValidBitStart",   UINT8),
    ("ValidBitLength",  UINT8),
    ("Reserved",        UINT16),
    ("HighIndex",       UINT32),
    ("Value",           UINT64)
  ]

class CPU_REGISTER_TABLE (Structure):
  _fields_ = [
    ("TableLength",         UINT32),
    ("NumberBeforeReset",   UINT32),
    ("AllocatedSize",       UINT32),
    ("InitialApicId",       UINT32),
    ("RegisterTableEntry",  POINTER(CPU_REGISTER_TABLE_ENTRY))
  ]

class ACPI_CPU_DATA (Structure):
  _fields_ = [
    ("StartupVector",             EFI_PHYSICAL_ADDRESS),
    ("GdtrProfile",               EFI_PHYSICAL_ADDRESS),
    ("IdtrProfile",               EFI_PHYSICAL_ADDRESS),
    ("StackAddress",              EFI_PHYSICAL_ADDRESS),
    ("StackSize",                 UINT32),
    ("NumberOfCpus",              UINT32),
    ("MtrrTable",                 EFI_PHYSICAL_ADDRESS),
    ("PreSmmInitRegisterTable",   EFI_PHYSICAL_ADDRESS),
    ("RegisterTable",             EFI_PHYSICAL_ADDRESS),
    ("ApMachineCheckHandlerBase", EFI_PHYSICAL_ADDRESS),
    ("ApMachineCheckHandlerSize", UINT32),
  ]

