from EfiPy                      import *

CPU_HOT_PLUG_DATA_REVISION_1      = 0x00000001

class CPU_HOT_PLUG_DATA (Structure):
  _fields_ = [
    ("Revision",    UINT32),
    ("ArrayLength", UINT32),
    ("ApicId",      POINTER(UINT64)),
    ("SmBase",      POINTER(UINTN)),
    ("Reserved",    UINT32),
    ("SmrrBase",    UINT32),
    ("SmrrSize",    UINT32),
  ]

