from EfiPy                      import *

SMM_DEFAULT_SMBASE           = 0x30000
SMM_HANDLER_OFFSET           = 0x8000
SMRAM_SAVE_STATE_MAP_OFFSET  = 0xfc00

class SMRAM_SAVE_STATE_MAP32 (Structure):
  _pack_   = 1
  _fields_ = [
    ("Reserved",        UINT8 * 0x200),
    ("Reserved1",       UINT8 * 0xF8),
    ("SMBASE",          UINT32),
    ("SMMRevId",        UINT32),
    ("IORestart",       UINT16),
    ("AutoHALTRestart", UINT16),
    ("Reserved2",       UINT8 *0x9C),
    ("IOMemAddr",       UINT32),
    ("IOMisc",          UINT32),
    ("_ES",             UINT32),
    ("_CS",             UINT32),
    ("_SS",             UINT32),
    ("_DS",             UINT32),
    ("_FS",             UINT32),
    ("_GS",             UINT32),
    ("Reserved3",       UINT32),
    ("_TR",             UINT32),
    ("_DR7",            UINT32),
    ("_DR6",            UINT32),
    ("_EAX",            UINT32),
    ("_ECX",            UINT32),
    ("_EDX",            UINT32),
    ("_EBX",            UINT32),
    ("_ESP",            UINT32),
    ("_EBP",            UINT32),
    ("_ESI",            UINT32),
    ("_EDI",            UINT32),
    ("_EIP",            UINT32),
    ("_EFLAGS",         UINT32),
    ("_CR3",            UINT32),
    ("_CR0",            UINT32),
  ]

class SMRAM_SAVE_STATE_MAP64 (Structure):
  _pack_   = 1
  _fields_ = [
    ("Reserved1",       UINT8 * 0x1D0),
    ("GdtBaseHiDword",  UINT32),
    ("LdtBaseHiDword",  UINT32),
    ("IdtBaseHiDword",  UINT32),
    ("Reserved2",       UINT8 * 0xC),
    ("IO_EIP",          UINT64),
    ("Reserved3",       UINT8 * 0x50),
    ("_CR4",            UINT32),
    ("Reserved4",       UINT8 * 0x48),
    ("GdtBaseLoDword",  UINT32),
    ("Reserved5",       UINT32),
    ("IdtBaseLoDword",  UINT32),
    ("Reserved6",       UINT32),
    ("LdtBaseLoDword",  UINT32),
    ("Reserved7",       UINT8 ),
    ("EptVmxControl",   UINT64),
    ("EnEptVmxControl", UINT32),
    ("Reserved8",       UINT8 * 0x14),
    ("SMBASE",          UINT32),
    ("SMMRevId",        UINT32),
    ("IORestart",       UINT16),
    ("AutoHALTRestart", UINT16),
    ("Reserved9[0x18]", UINT8 * 0x18),
    ("_R15",            UINT64),
    ("_R14",            UINT64),
    ("_R13",            UINT64),
    ("_R12",            UINT64),
    ("_R11",            UINT64),
    ("_R10",            UINT64),
    ("_R9",             UINT64),
    ("_R8",             UINT64),
    ("_RAX",            UINT64),
    ("_RCX",            UINT64),
    ("_RDX",            UINT64),
    ("_RBX",            UINT64),
    ("_RSP",            UINT64),
    ("_RBP",            UINT64),
    ("_RSI",            UINT64),
    ("_RDI",            UINT64),
    ("IOMemAddr",       UINT64),
    ("IOMisc",          UINT32),
    ("_ES",             UINT32),
    ("_CS",             UINT32),
    ("_SS",             UINT32),
    ("_DS",             UINT32),
    ("_FS",             UINT32),
    ("_GS",             UINT32),
    ("_LDTR",           UINT32),
    ("_TR",             UINT32),
    ("_DR7",            UINT64),
    ("_DR6",            UINT64),
    ("_RIP",            UINT64),
    ("IA32_EFER",       UINT64),
    ("_RFLAGS",         UINT64),
    ("_CR3",            UINT64),
    ("_CR0",            UINT64),
  ]

class SMRAM_SAVE_STATE_MAP (Union):
  _fields_ = [
    ("x86", SMRAM_SAVE_STATE_MAP32),
    ("x64", SMRAM_SAVE_STATE_MAP64),
  ]

SMRAM_SAVE_STATE_MIN_REV_ID_IOMISC  = 0x30004

SMM_IO_LENGTH_BYTE             = 0x01
SMM_IO_LENGTH_WORD             = 0x02
SMM_IO_LENGTH_DWORD            = 0x04

SMM_IO_TYPE_IN_IMMEDIATE       = 0x9
SMM_IO_TYPE_IN_DX              = 0x1
SMM_IO_TYPE_OUT_IMMEDIATE      = 0x8
SMM_IO_TYPE_OUT_DX             = 0x0
SMM_IO_TYPE_INS                = 0x3
SMM_IO_TYPE_OUTS               = 0x2
SMM_IO_TYPE_REP_INS            = 0x7
SMM_IO_TYPE_REP_OUTS           = 0x6

class SMRAM_SAVE_STATE_IOMISC_Bits (Structure):
  _pack_   = 1
  _fields_ = [
    ("SmiFlag",   UINT32, 1),
    ("Length",    UINT32, 3),
    ("Type",      UINT32, 4),
    ("Reserved1", UINT32, 8),
    ("Port",      UINT32, 16),
  ]

class SMRAM_SAVE_STATE_IOMISC (Union):
  _fields_ = [
    ("Bits",    SMRAM_SAVE_STATE_IOMISC_Bits),
    ("Uint32",  UINT32),
  ]

