from EfiPyAcpiBase import AcpiTables
from EfiPy.MdePkg.IndustryStandard.Acpi10 import EFI_ACPI_DESCRIPTION_HEADER
from EfiPy.MdePkg.IndustryStandard.Acpi50 import EFI_ACPI_5_0_BOOT_GRAPHICS_RESOURCE_TABLE

BgrtSize    = 0
BgrtAddress = 0

for _Signature, _Address, _Length in AcpiTables:
  if _Signature == 'BGRT':
    BgrtAddress = _Address
    DescHeader = EFI_ACPI_DESCRIPTION_HEADER.from_address (BgrtAddress)
    BgrtSize    = _Length
    break;

if BgrtAddress == 0:
  raise ImportError ('BGRT Not found')

DescHeader  = EFI_ACPI_5_0_BOOT_GRAPHICS_RESOURCE_TABLE.from_address (BgrtAddress)

Address   = BgrtAddress
Table     = DescHeader
Size      = BgrtSize
Revision  = DescHeader.Header.Revision
