from EfiPyAcpiBase import AcpiTables

FacsSize    = 0
FacsAddress = 0

for _Signature, _Address, _Length in AcpiTables:
  if _Signature == 'FACS':
    FacsAddress = _Address
    FacsSize    = _Length
    break;

if FacsAddress == 0:
  raise ImportError ('FACS Not found')

from EfiPy.MdePkg.IndustryStandard.Acpi20 import EFI_ACPI_2_0_FIRMWARE_ACPI_CONTROL_STRUCTURE
DescHeader = EFI_ACPI_2_0_FIRMWARE_ACPI_CONTROL_STRUCTURE.from_address (FacsAddress)

_Revision = 0
if    DescHeader.Version == 0:
  from EfiPy.MdePkg.IndustryStandard.Acpi10 import EFI_ACPI_1_0_FIRMWARE_ACPI_CONTROL_STRUCTURE
  DescHeader = EFI_ACPI_1_0_FIRMWARE_ACPI_CONTROL_STRUCTURE.from_address (FacsAddress)
elif DescHeader.Version == 1:
  _Revision = 1
elif DescHeader.Version == 2:
  from EfiPy.MdePkg.IndustryStandard.Acpi40 import EFI_ACPI_4_0_FIRMWARE_ACPI_CONTROL_STRUCTURE
  DescHeader = EFI_ACPI_4_0_FIRMWARE_ACPI_CONTROL_STRUCTURE.from_address (FacsAddress)
  _Revision = 2

Address   = FacsAddress
Table     = DescHeader
Size      = FacsSize
Revision  = _Revision
