import EfiPy
from EfiPyAcpiBase import AcpiTables
from EfiPy.MdePkg.IndustryStandard  import EFIPY_INDUSTRY_STRUCTURE
from EfiPy.MdePkg.IndustryStandard.MemoryMappedConfigurationSpaceAccessTable import EFI_ACPI_MEMORY_MAPPED_ENHANCED_CONFIGURATION_SPACE_BASE_ADDRESS_ALLOCATION_STRUCTURE as MCFG_ENTRY
from EfiPy.MdePkg.IndustryStandard.MemoryMappedConfigurationSpaceAccessTable import EFI_ACPI_MEMORY_MAPPED_CONFIGURATION_BASE_ADDRESS_TABLE_HEADER as MCFG_HEADER

McfgSize    = 0
McfgAddress = 0

for _signature, _address, _size in AcpiTables:
  if _signature == 'MCFG':
    McfgAddress = _address
    _McfgHeader = MCFG_HEADER.from_address (McfgAddress)
    McfgSize    = _size
    break;

if McfgAddress == 0:
  raise ImportError ('MCFG Not found')

class MCFG_TABLE_CLASS (EFIPY_INDUSTRY_STRUCTURE):
  _fields_ = [
    ("Header",    MCFG_HEADER),
    ("McfgEntry", MCFG_ENTRY * ((McfgSize - EfiPy.sizeof (MCFG_HEADER)) / EfiPy.sizeof (MCFG_ENTRY)))
  ]

Address   = McfgAddress
Size      = McfgSize
Table     = MCFG_TABLE_CLASS.from_address (McfgAddress)
Revision  = Table.Header.Header.Revision