from EfiPyAcpiBase import AcpiTables
from EfiPy.MdePkg.IndustryStandard.Acpi10 import EFI_ACPI_DESCRIPTION_HEADER

import struct
import EfiPy
from EfiPy.MdePkg.IndustryStandard        import EFIPY_INDUSTRY_STRUCTURE
from EfiPy.MdePkg.IndustryStandard.Acpi10 import EFI_ACPI_DESCRIPTION_HEADER

Address   = 0
Table     = None
Size      = 0
Revision  = 0

def BuildRsdt (Rsdp):

  global Address
  global Table
  global Size
  global Revision

  _Rsdt = EFI_ACPI_DESCRIPTION_HEADER.from_address (Rsdp.RsdtAddress)
  class ROOT_SYSTEM_DESCRIPTION_TABLE (EFIPY_INDUSTRY_STRUCTURE):
    _fields_ = [
      ("Signature",         EfiPy.UINT32),
      ("Length",            EfiPy.UINT32),
      ("Revision",          EfiPy.UINT8),
      ("Checksum",          EfiPy.UINT8),
      ("OemId",             EfiPy.UINT8 * 6),
      ("OemTableId",        EfiPy.UINT64),
      ("OemRevision",       EfiPy.UINT32),
      ("CreatorId",         EfiPy.UINT32),
      ("CreatorRevision",   EfiPy.UINT32),
      ("DescriptionEntry",  EfiPy.UINT32 * ((_Rsdt.Length - EfiPy.sizeof (EFI_ACPI_DESCRIPTION_HEADER)) / EfiPy.sizeof (EfiPy.UINT32)))
    ]
  _Rsdt = ROOT_SYSTEM_DESCRIPTION_TABLE.from_address (Rsdp.RsdtAddress)

  Address   = Rsdp.RsdtAddress
  Table     = _Rsdt
  Size      = _Rsdt.Length
  Revision  = _Rsdt.Revision

  return _Rsdt
