from rUnionOp import COMMON_REG_32BITS_OUTPUT, rUnionOp
from BitOp import _MemArray, _MemCell
import _LoLeOp as _l
import EfiPy as _e

class EFIPY_MSR_COMMON_Bits (_e.Structure):
  _fields_ = [
    ("Reserved",  _e.UINT64, 64),
  ]

class EFIPY_MSR_COMMON_Reg (_e.Union):
  _fields_ = [
    ("Bits",      EFIPY_MSR_COMMON_Bits),
    ("Uint32",    _e.UINT32),
    ("Uint64",    _e.UINT64),
  ]

class rMsr (rUnionOp):

  def MemGet (self, Key):
    ret = _l.RdMsr (Key)
    self.KeyObj.__setattr__ (self.CellType, ret)
    return ret

  def MemSet (self, Key, Value):
    _l.WrMsr (Key, Value)
    self.KeyObj.__setattr__ (self.CellType, Value)

CellArray = {}

CellExt = {
  # "CellUnion": EFIPY_MSR_COMMON_Reg,
  "CellDefault": EFIPY_MSR_COMMON_Reg,
  "CellArray": CellArray,
  "CellType":  'Uint64',
  "CellBits":  'Bits'
}

msr = _MemArray("msr",  64, CellClass = rMsr, CellExt = CellExt)
